import React, { useState } from "react";
import Button from "../UI/Button";
import Card from "../UI/Card";
import classes from "./AddUser.module.css";
import ErrorModal from "../UI/ErrorModal";
import cla from "../UI/ErrorModal.module.css";

const AddUser = (props) => {
  const [enteredUsername, setEnteredUsername] = useState("");
  const [enteredAge, setEnteredAge] = useState("");
  const [errorState, setErrorState] = useState();
  const [errorModal, setErrorModal] = useState(false);

  const usernameChangeHandler = (e) => {
    setEnteredUsername(e.target.value);
  };

  const ageChangeHandler = (e) => {
    setEnteredAge(e.target.value);
  };
  const formSubmitHandler = (e) => {
    e.preventDefault();
    if (enteredUsername.trim().length === 0 || enteredAge.trim().length === 0) {
      setErrorState({
        title: "Invalid input",
        message: "Please enter valid name and age (no empty spaces)",
      });
      return;
    }
    if (+enteredAge < 1) {
      setErrorState({
        title: "Invalid input",
        message: "Age must be (> 1)",
      });
      return;
    }

    props.userDetails(enteredUsername, enteredAge);
    setEnteredAge("");
    setEnteredUsername("");
  };

  const modalStatehandler = () => {
    setErrorModal(true);
  };

  return (
    <div>
      {!errorModal && (
        <div>
          <div className={cla.backdrop} />

          <ErrorModal
            title={errorState.title}
            message={errorState.message}
            errorModal={errorModal}
            modalHandler={modalStatehandler}
          />
        </div>
      )}
      {errorModal && (
        <Card className={classes.input}>
          <form onSubmit={formSubmitHandler}>
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              value={enteredUsername}
              onChange={usernameChangeHandler}
            />
            <label htmlFor="age">Age</label>
            <input
              type="number"
              id="age"
              value={enteredAge}
              onChange={ageChangeHandler}
            />
            <Button type="submit">Add User</Button>
          </form>
        </Card>
      )}
    </div>
  );
};

export default AddUser;
